pub fn min_window2(s: String, t: String) -> String {
    let mut lptr = 0;
    let mut freqmap: HashMap<char, i32> = HashMap::new();
    let mut window_map: HashMap<char, i32> = HashMap::new();

    for i in t.chars() {
        *freqmap.entry(i).or_insert(0) += 1;
    }
    let need = freqmap.len();
    let mut have = 0;
    let mut ans: Option<(usize, usize)> = None; // ! option is really required
    for (rptr, char) in s.chars().enumerate() {
        *window_map.entry(char).or_insert(0) += 1;
        match (freqmap.get(&char), window_map.get(&char)) {
            (Some(freq), Some(window)) if freq == window => have += 1,
            _ => (),
        }
        while have == need {
            match &ans {
                Some((l, r)) => {
                    if r - l > rptr - lptr {
                        ans = Some((lptr, rptr))
                    }
                }
                _ => ans = Some((lptr, rptr)),
            }
            let l_char = s.chars().nth(lptr).unwrap();
            *window_map.entry(l_char).or_insert(0) -= 1;
            match (window_map.get(&l_char), freqmap.get(&l_char)) {
                (Some(window), Some(freq)) if window < freq => have -= 1,
                _ => (),
            }
            lptr += 1;
        }
    }
    if let Some((l, r)) = ans {
        s.get(l..r + 1).get_or_insert("").to_string()
    } else {
        String::from("")
    }
}
// hashmap is slower
pub fn min_window(s: String, t: String) -> String {
    let mut freqmap: HashMap<char, i32> = HashMap::new();
    let mut windowmap: HashMap<char, i32> = HashMap::new();
    let mut ans: Option<(usize, usize)> = None;
    let mut lptr = 0;
    let mut have = 0;
    for i in t.chars() {
        *freqmap.entry(i).or_insert(0) += 1;
    }
    let need = freqmap.len();

    for (rptr, char) in s.chars().enumerate() {
        *windowmap.entry(char).or_insert(0) += 1;
        if freqmap.get(&char).is_some() && freqmap.get(&char) == windowmap.get(&char) {
            have += 1;
        }
        while have == need {
            // dbg!(&ans);

            match &ans {
                Some((l, r)) if r - l < rptr - lptr => (),
                _ => ans = Some((lptr, rptr)),
            }

            let leftchar = s.chars().nth(lptr).unwrap();
            *windowmap.entry(leftchar).or_insert(0) -= 1;
            if freqmap.get(&leftchar).is_some() && windowmap.get(&leftchar) < freqmap.get(&leftchar)
            {
                have -= 1
            }
            lptr += 1
        }
    }
    ans.map_or(String::from(""), |(l, r)| {
        s.get(l..r + 1).unwrap_or("").into()
    })
}
